import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }

  increment = () => {
    this.setState((prevState) => ({ count: prevState.count + 1 }));
  };

  decrement = () => {
    this.setState((prevState) => ({ count: prevState.count - 1 }));
  };

  reset = () => {
    this.setState({ count: 0 });
  };

  render() {
    return (
      <div className="container">
        <div className="counter-container">
          <button onClick={this.decrement} className="button">-</button>
          <span className="counter">{this.state.count}</span>
          <button onClick={this.increment} className="button">+</button>
        </div>
        <button onClick={this.reset} className="reset-button">Reset</button>
      </div>
    );
  }
}

export default App;
