import React, { useState } from 'react';

function App() {

  const [count, setCount] = useState(0);


  const increment = () => {
    setCount(count + 1);
  };


  const decrement = () => {
    setCount(count - 1);
  };


  const reset = () => {
    setCount(0);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', paddingTop: '50px' }}>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={decrement} style={{ marginRight: '10px', padding: '10px' }}>-</button>
        <span style={{ fontSize: '30px', margin: '0 20px' }}>{count}</span>
        <button onClick={increment} style={{ marginLeft: '10px', padding: '10px' }}>+</button>
      </div>
      <button onClick={reset} style={{ padding: '10px', fontSize: '18px' }}>Reset</button>
    </div>
  );
}

export default App;
